export class Main {
    main() {
        let deck = this.createDeck();
        deck = this.shuffle(deck);
        console.log(deck);
    }
    createDeck() {
        let deck = new Array();
        for (let i = 1; i <= 5; i++) {
            deck.push(i);
        }
        return deck;
    }
    shuffle(arr) {
        let size = arr.length;
        for (let i = size; i > 0; i--) {
            let ran = Math.floor(Math.random() * i);
            arr.push(arr[ran]);
            arr.splice(ran, 1);
        }
        return arr;
    }
}
